const a11yScan = require('../utils/accessibilityScan')
const { A11Y_SCAN } = process.env
const { successSelector } = require('../config/selectors')

module.exports = {
  '@disabled': A11Y_SCAN ? !JSON.parse(A11Y_SCAN) : true,
  before: function (browser) {
    const KUI =  browser.page.KUI()
    const loginPage = browser.page.LoginPage()
    if (!process.env.TEST_LOCAL) {
      loginPage.navigate()
      loginPage.authenticate()
    }
    KUI.navigate()
    KUI.waitForPageLoad(browser)
    KUI.verifyWebsocketConnection(browser)
  },

  'KUI Tables: Run Accessibility Scan': browser => {
    runBothThemes(browser, 'kubectl get pods -n kube-system', 'kui-tables')
  },

  'KUI Tables-CLI: Run Accessibility Scan': browser => {
    runBothThemes(browser, 'cloudctl', 'kui-tables-cli')
  },

  'KUI Sidecar: Run Accessibility Scan': browser => {
    runBothThemes(browser,'kubectl get deployment mcm-kui -o yaml -n kube-system', 'kui-sidecar')
  },

  'KUI Sidecar-Helm: Run Accessibility Scan': browser => {
    runBothThemes(browser, 'helm get mcm-kui', 'kui-helm')
  },


  after: function (browser, done) {
    setTimeout(() => {
      if (browser.sessionId) {
        browser.end()
        done()
      } else {
        done()
      }
    })
  }
}

function exec(browser, command, name) {
  const KUI =  browser.page.KUI()
  KUI.executeCommand(browser, command)
  a11yScan.runAccessibilityScan(browser, name)
}

function runBothThemes(browser, command, name) {
  const KUI =  browser.page.KUI()
  KUI.executeCommand(browser, 'themes')
  browser.click(successSelector + ` .clickable span[title="IBM Light"]`)
  exec(browser, command, name)

  KUI.executeCommand(browser, 'themes')
  browser.click(successSelector + ` .clickable span[title="IBM Dark"]`)
  exec(browser, command, name + '-dark')
}