/**
 * Text to be displayed in the sidecar toolbar
 *
 */
declare type ToolbarTextType = 'info' | 'success' | 'warning' | 'error';
declare type ToolbarTextValue = string | Element;
export interface ToolbarAlert {
    type: ToolbarTextType;
    title: string;
    body?: string;
}
export interface ToolbarText {
    type: ToolbarTextType;
    text: ToolbarTextValue;
    alerts?: ToolbarAlert[];
}
export {};
